import { useState, useEffect } from "react";
const Banner = (props) => {
  //state bộ phim dùng làm banner
  const [movie, setMovie] = useState({});
  //Tạo hàm lấy ngẫu nhiên thông tin của 1 bộ phim trong 20 bộ phim lấy từ API fetchNetflixOriginals để hiển thị lên Banner
  const randomMovie = () => {
    fetch(`https://api.themoviedb.org/3${props.linkapi}`)
      .then((response) => response.json())
      .then((data) =>
        setMovie(
          data.result[Math.floor(Math.random() * data.result.length - 1)]
        )
      );
  };
  //tìm nạp dữ liệu ngay sau khi tải trang; useEffect sẽ thực thi lại khi hàm randomMovie thay đổi
  useEffect(() => {
    randomMovie(), [randomMovie];
  });
  return (
    <div>
      <img src={movie.backdrop_path} alt="movieimage" />
      <p>{movie.title}</p>
      <div>
        <button>Play</button>
        <button>My list</button>
      </div>
      <p>{movie.overview}</p>
    </div>
  );
};

export default Banner;
