import React from "react";

//Tạo component để hiển thị thông tin phim được chọn
const MovieDetail = (props) => {
  //Tạo hàm để lấy dữ liệu về trailer của phim được chọn
  let dataresults;
  let videoYoutube = [];
  const id = props.movieid;
  const movietrailer = (id) => {
    fetch(
      `https://api.themoviedb.org/3//movie/${id}/videos?api_key=b96d917db4e811cda60de6da46080907`
    )
      .then((response) => response.json())
      .then((data) => (dataresults = data.results))
      .catch((err) => alert(err));
    //Lọc ra những đối tượng trong mảng vừa trả về phù hợp với yêu cầu và thêm vào mảng mới
    videoYoutube = dataresults.filter(
      (video) =>
        video.site === "YouTube" &&
        (video.type === "Teaser" || video.type === "Trailer")
    );
  };
  movietrailer(id);
  return (
    <div>
      <div>
        <p>{props.moviename}</p>
        <hr />
        <p>Release Date: {props.releasedate}</p>
        <p>Vote: ${props.voteaverage}/10</p>
        <p>{props.overview2}</p>
      </div>
      <div>
        {/* Nếu mảng videoYoutube có video thì lấy video đầu tiên tìm được,
    nếu không có video nào thỏa mãn thì sẽ hiển thị ảnh Backdrop */}
        {videoYoutube.length == 0 && <img src={props.path} alt="movieimage" />}
        {videoYoutube.length > 0 && (
          <iframe
            width="100%"
            height="400"
            src={`https://www.youtube.com/embed/${videoYoutube[1].key}`}
          ></iframe>
        )}
      </div>
    </div>
  );
};

export default MovieDetail;
