import React, { useCallback, useEffect, useState } from "react";
import styles from "./MovieDetail.module.css";
//Tạo component để hiển thị thông tin phim được chọn
const MovieDetail = (props) => {
  //lấy dữ liệu về trailer của bộ phim
  //state dữ liệu trả về
  const [videos, setVideos] = useState([]);
  useCallback(() => {
    fetch(
      `https://api.themoviedb.org/3//movie/${props.movieData.id}/videos?api_key=b96d917db4e811cda60de6da46080907`
    )
      .then((response) => response.json())
      .then((data) => setVideos(data.results))
      .catch((err) => alert(err));
  }, [props.movieData.id]);

  return (
    <div>
      <div>
        <p>
          {props.movieData.name ? props.movieData.name : props.movieData.title}
        </p>
        <hr />
        <p>Release Date: {props.movieData.first_air_date}</p>
        <p>Vote: ${props.movieData.vote_average}/10</p>
        <p>{props.movieData.overview}</p>
      </div>
      <div>
        {/* Nếu mảng videoYoutube có video thì lấy video đầu tiên tìm được,
    nếu không có video nào thỏa mãn thì sẽ hiển thị ảnh Backdrop */}
        {videoYoutube.length == 0 && <img src={props.path} alt="movieimage" />}
        {videoYoutube.length > 0 && (
          <iframe
            width="100%"
            height="400"
            src={`https://www.youtube.com/embed/${videoYoutube[1].key}`}
          ></iframe>
        )}
      </div>
    </div>
  );
};

export default MovieDetail;
