import React, { useCallback, useEffect, useState } from "react";

import MovieDetail from "./MovieDetail.jsx";

import styles from "./MovieList.module.css";

//component hiển thị danh sách phim theo danh mục
const MovieList = (props) => {
  //state dữ liệu phim lấy được theo danh mục lần lượt là Original, Xu hướng, Xếp hạng cao, Hành động, Hài, Kinh dị, Lãng mạn, Tài liệu
  const [originalmovies, setOriginalmovies] = useState([]);
  const [trendingmovies, setTrendingmovies] = useState([]);
  const [topratedmovies, setTopratedmovies] = useState([]);
  const [actionmovies, setActionmovies] = useState([]);
  const [comedymovies, setComedylmovies] = useState([]);
  const [horrormovies, setHorrormovies] = useState([]);
  const [romancemovies, setRomancemovies] = useState([]);
  const [documentaries, setDocumentaries] = useState([]);

  //Tạo hàm lấy phim theo từng danh mục từ API tương ứng

  //danh mục Original
  const originalmovieFetch = useCallback(() => {
    fetch(`https://api.themoviedb.org/3${props.linkapioriginal}`)
      .then((response) => response.json())
      .then((data) => setOriginalmovies(data.results))
      .catch((err) => alert(err));
  }, []);
  //tìm nạp dữ liệu ngay sau khi tải trang, phía trên có thêm useCallback để không bị lặp vô hạn
  useEffect(() => {
    originalmovieFetch();
  }, [originalmovieFetch]);

  //danh mục Xu hướng
  const trendingmovieFetch = useCallback(() => {
    fetch(`https://api.themoviedb.org/3${props.linkapitrending}`)
      .then((response) => response.json())
      .then((data) => setTrendingmovies(data.results))
      .catch((err) => alert(err));
  }, []);
  //tìm nạp dữ liệu ngay sau khi tải trang, phía trên có thêm useCallback để không bị lặp vô hạn
  useEffect(() => {
    trendingmovieFetch();
  }, [trendingmovieFetch]);

  //danh mục Xếp hạng cao
  const topratedmovieFetch = useCallback(() => {
    fetch(`https://api.themoviedb.org/3${props.linkapitoprated}`)
      .then((response) => response.json())
      .then((data) => setTopratedmovies(data.results))
      .catch((err) => alert(err));
  }, []);
  //tìm nạp dữ liệu ngay sau khi tải trang, phía trên có thêm useCallback để không bị lặp vô hạn
  useEffect(() => {
    topratedmovieFetch();
  }, [topratedmovieFetch]);

  //danh mục Hành động
  const actionmovieFetch = useCallback(() => {
    fetch(`https://api.themoviedb.org/3${props.linkapiaction}`)
      .then((response) => response.json())
      .then((data) => setActionmovies(data.results))
      .catch((err) => alert(err));
  }, []);
  //tìm nạp dữ liệu ngay sau khi tải trang, phía trên có thêm useCallback để không bị lặp vô hạn
  useEffect(() => {
    actionmovieFetch();
  }, [actionmovieFetch]);

  //danh mục Hài
  const comedymovieFetch = useCallback(() => {
    fetch(`https://api.themoviedb.org/3${props.linkapicomedy}`)
      .then((response) => response.json())
      .then((data) => setComedylmovies(data.results))
      .catch((err) => alert(err));
  }, []);
  //tìm nạp dữ liệu ngay sau khi tải trang, phía trên có thêm useCallback để không bị lặp vô hạn
  useEffect(() => {
    comedymovieFetch();
  }, [comedymovieFetch]);

  //danh mục Kinh dị
  const horrormovieFetch = useCallback(() => {
    fetch(`https://api.themoviedb.org/3${props.linkapihorror}`)
      .then((response) => response.json())
      .then((data) => setHorrormovies(data.results))
      .catch((err) => alert(err));
  }, []);
  //tìm nạp dữ liệu ngay sau khi tải trang, phía trên có thêm useCallback để không bị lặp vô hạn
  useEffect(() => {
    horrormovieFetch();
  }, [horrormovieFetch]);

  //danh mục Lãng mạn
  const romancemovieFetch = useCallback(() => {
    fetch(`https://api.themoviedb.org/3${props.linkapiromance}`)
      .then((response) => response.json())
      .then((data) => setRomancemovies(data.results))
      .catch((err) => alert(err));
  }, []);
  //tìm nạp dữ liệu ngay sau khi tải trang, phía trên có thêm useCallback để không bị lặp vô hạn
  useEffect(() => {
    romancemovieFetch();
  }, [romancemovieFetch]);

  //danh mục Tài liệu
  const documentarieFetch = useCallback(() => {
    fetch(`https://api.themoviedb.org/3${props.linkapidoc}`)
      .then((response) => response.json())
      .then((data) => setDocumentaries(data.results))
      .catch((err) => alert(err));
  }, []);
  //tìm nạp dữ liệu ngay sau khi tải trang, phía trên có thêm useCallback để không bị lặp vô hạn
  useEffect(() => {
    documentarieFetch();
  }, [documentarieFetch]);

  //đặt trạng thái false ẩn true hiện cho component MovieDetail
  const [state, setState] = useState(false);

  let seeMore;
  let movie1;
  let movie2;
  let movie3;
  let movie4;
  let movie5;
  let movie6;
  let movie7;
  let movie8;
  //Tạo hàm dùng cho sự kiện onClick của <img>
  const chosenMovie = (event) => {
    setState(true);
    //lấy id của phim được chọn
    seeMore = event.target.getAttribute("movid");
    // console.log(seeMore);
    movie1 = originalmovies.filter((movie) => movie.id === seeMore);
    movie2 = trendingmovies.filter((movie) => movie.id === seeMore);
    movie3 = topratedmovies.filter((movie) => movie.id === seeMore);
    movie4 = actionmovies.filter((movie) => movie.id === seeMore);
    movie5 = comedymovies.filter((movie) => movie.id === seeMore);
