import React from "react";

//Tạo component để hiển thị thông tin phim được chọn
const MovieDetail = (props) => {
  return (
    <div>
      <div>
        <p>{props.moviename}</p>
        <hr />
        <p>Release Date: {props.releasedate}</p>
        <p>Vote: ${props.voteaverage}/10</p>
        <p>{props.overview2}</p>
      </div>
      <div>
        <iframe
          width="100%"
          height="400"
          src={`https://www.youtube.com/embed/${props.moviekey}`}
        ></iframe>
      </div>
    </div>
  );
};

export default MovieDetail;
