import React, { useState, useEffect, useCallback } from "react";

const Banner = (props) => {
  //state bộ phim dùng làm banner
  const [movie, setMovie] = useState({});
  //Tạo hàm lấy ngẫu nhiên thông tin của 1 bộ phim trong 20 bộ phim lấy từ API fetchNetflixOriginals để hiển thị lên Banner
  const randomMovie = useCallback(() => {
    fetch(`https://api.themoviedb.org/3${props.linkapi}`)
      .then((response) => response.json())
      .then((data) =>
        setMovie(
          data.results[Math.floor(Math.random() * data.results.length - 1)]
        )
      )
      .catch((err) => alert(err));
  }, []);
  //tìm nạp dữ liệu ngay sau khi tải trang; useEffect sẽ thực thi lại khi hàm randomMovie thay đổi
  useEffect(() => {
    randomMovie();
  }, [randomMovie]);
  return (
    <div>
      <img
        src={`https://image.tmdb.org/t/p/w1920${movie.backdrop_path}`}
        alt="movieimage"
      />
      <p>{`${movie.name}`}</p>
      <button>Play</button>
      <button>My List</button>
      <p>{`${movie.overview}`}</p>
    </div>
  );
};

export default Banner;
