//Tạo component NavBar để xây dựng giao diện bao gồm phần chữ "Movie App" và nút Search
import React, { useState } from "react";
import styles from "./NarBar.module.css";

//Tạo hàm để chuyển về trang chủ dùng cho sự kiện onClick của thẻ p với nội dung "Movie App"
const backToHomePage = () => {
  window.location.replace("/");
};

//Tạo hàm để chuyển về trang tìm kiếm dùng cho sự kiện onClick của nút Search tạo bởi thẻ svg
const backToSearchPage = () => {
  window.location.replace("/search");
};

const NavBar = () => {
  //state màu nền Navbar
  const [color, setColor] = useState("styles.transparent");
  //Bắt sự kiện scroll của Window
  window.addEventListener("scroll", function () {
    //nếu như window.scrollY lớn hơn 100px thì nền đen
    if (window.scrollY > 100) {
      setColor("styles.blackBackground");
    }
  });
  return (
    <div className={color}>
      <p onClick={backToHomePage}>Movie App</p>
      <svg
        className={`svg-inline--fa fa-search fa-w-16 ${styles.btnSearch}`}
        fill="#ccc"
        aria-hidden="true"
        data-prefix="fas"
        data-icon="search"
        role="img"
        xmlns="http://www.w3.org/2000/svg"
        viewBox="0 0 512 512"
        onClick={backToSearchPage}
      >
        <path d="M505 442.7L405.3 343c-4.5-4.5-10.6-7-17-7H372c27.6-35.3 44-79.7 44-128C416 93.1 322.9 0 208 0S0 93.1 0 208s93.1 208 208 208c48.3 0 92.7-16.4 128-44v16.3c0 6.4 2.5 12.5 7 17l99.7 99.7c9.4 9.4 24.6 9.4 33.9 0l28.3-28.3c9.4-9.4 9.4-24.6.1-34zM208 336c-70.7 0-128-57.2-128-128 0-70.7 57.2-128 128-128 70.7 0 128 57.2 128 128 0 70.7-57.2 128-128 128z"></path>
      </svg>
      <p>
        Mỗi đồ vật đều mang một hình dáng khác nhau và đặc trưng khác nhau. Ngày
        hôm nay bố em mới mua cho nhà em một bộ bàn ghế để làm chỗ tiếp khách,
        em nhảy nhót vui sướng khi thấy được bộ bàn ghế đó. Thế là từ nay nhà em
        đã có bộ bàn ghế mới em thấy vui sướng làm sao. Bộ bàn ghế ấy có màu
        vàng thẫm, cái màu vàng ong ong chứ không vàng chói trông mới thật sang
        trọng làm sao. Bộ bàn ghế ấy có bốn cái ghế và một cái bàn. Cái ghế dài
        nhất được đặt ngang ra dựa vào tường nhà em. Nhìn nó như một ông hoàng
        trong nhà với dáng hình to lớn và bộ áo vàng hoành tráng sang trọng ấy.
        Hai chiếc ghế đơn thì vuông vắn hơn nó không dài không hình chữ nhật
        nhưng cũng có thể dành cho hai người ngồi vào đó. Thật sự rất đẹp, thêm
        đó là một chiếc ghế nhỏ nữa, không tựa mà chỉ đơn giản là ghế ngồi bình
        thường thôi nhưng phần dưới vẫn được khắc những nét chạm trổ như những
        chiếc ghế to khác. Một chiếc bàn ở giữa, hình chữ nhật cao hơn so với
        ghế ngồi một chút trong nó thật đẹp làm sao. Chao ôi chỉ nhìn thôi là em
        đã thấy sung sướng hết cả lên rồi. Những nét chạm trổ trên ghế thật đẹp
        đó là hình ảnh của những bông hoa trên phần tựa của ghế, những phần hoa
        ấy được khắc nổi lên trên bề mặt tựa nhưng rất mỏng không khiến cho
        chúng ta đau mỗi khi ngồi tựa vào đó. Những chiếc ghế tựa khi ngồi còn
        có hai tay vịn nữa, hai cái chỗ ấy phình ra trông thật đẹp làm sao. Như
        kiểu ghế dành cho quan lại vua chúa ngày xưa ngồi vậy. Bên dưới những
        hình con long con phượng được khắc trên những chỗ tựa cao nhất nhìn
        những đường nét mềm mại mới thấy được sự điêu luyện của những bàn tay
        nghệ nhân. Những khúc gỗ to đùng sần sùi như thế mà họ vẫn có thể khắc
        nên những con rồng con phượng tuyệt đẹp đến như vậy. Nó không cứng nhắc
        như khúc gỗ mà mềm mại như một con rồng thật vậy. Phần để dựa đầu vào
        được làm cho mềm mại nhẵn nhụi làm cho ai tựa lên đó cũng có cảm giác dễ
        chịu và không bị đau đầu. Bộ bàn ghế mới ấy thật sự rất đẹp, nó không
        chỉ lấp được khoảng trống trong chính căn nhà em mà nó còn mang đến sự
        sung túc và đầy đủ nữa. Nhìn vào căn phòng khác với bộ bàn ghế mới trong
        thật sự là đẹp mắt và sang trọng. tối hôm ấy khi ăn cơm xong cả gia đình
        em ngồi quây quần bên nhau trên chiếc ghế ấy để xem những bộ phim hay.
        Hình như ngồi trên chiếc ghế ấy em thấy bộ phim hôm ấy hay hơn thấy gia
        đình có một sự hạnh phúc không hề nhỏ. Thằng em của em không biết chắc
        là ghế em quá cho nên nó đã ngủ thiếp đi lúc nào không biết. Nó kê đầu
        trên đùi mẹ mà ngủ một cách ngon lành. Bố mẹ cùng em ngồi xem hết bộ
        phim bố không thôi thỉnh thoảng ngắm nghía lại những đường nét trên ghế.
        Mẹ thì trêu bố và cười thật vui vẻ. Em cảm thấy bộ bàn ghế mới đã gắn
        kết gia đình em, giúp gia đình em có một không gian thật tuyệt vời và
        hạnh phúc bên nhau.
      </p>
    </div>
  );
};

export default NavBar;
