import { useState, useEffect } from "react";
const Banner = () => {
  //state bộ phim dùng làm banner
  const [movie, setMovie] = useState({});
  //Tạo hàm lấy ngẫu nhiên thông tin của 1 bộ phim trong 20 bộ phim lấy từ API fetchNetflixOriginals để hiển thị lên Banner
  const randomMovie = () => {
    fetch(
      `https://api.themoviedb.org/3/discover/tv?api_key=b96d917db4e811cda60de6da46080907&with_network=123`
    )
      .then((response) => response.json())
      .then((data) =>
        setMovie(
          data.results[Math.floor(Math.random() * data.results.length - 1)]
        )
      )
      .then(console.log(movie))
      .catch((err) => alert(err));
  };
  //tìm nạp dữ liệu ngay sau khi tải trang; useEffect sẽ thực thi lại khi hàm randomMovie thay đổi
  useEffect(() => {
    randomMovie();
  }, [randomMovie]);
  return <div>{`${movie.title}`}</div>;
};

export default Banner;
